CREATE OR REPLACE FUNCTION obter_se_conta_prov (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

					 
qt_conta_prov_w		integer;
ie_provisoria_w     varchar(1);


BEGIN 
 
ie_provisoria_w     := 'N';
 
select	count(*) 
into STRICT	qt_conta_prov_w 
from	conta_paciente 
where	nr_atendimento		= nr_atendimento_p 
and	ie_status_acerto	= 1;
 
if (qt_conta_prov_w > 0) then 
	ie_provisoria_w := 'S';
 
end if;
 
 
return ie_provisoria_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
