CREATE OR REPLACE FUNCTION obter_se_conta_titulo_aberto ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(1);
nr_seq_protocolo_w	bigint;


BEGIN 
 
select	max(a.nr_seq_protocolo) 
into STRICT	nr_seq_protocolo_w 
from	conta_paciente a 
where	a.nr_interno_conta	= nr_interno_conta_p;
 
select	coalesce(max('S'),'N') 
into STRICT	ie_retorno_w 
from (SELECT	1 
	from	titulo_receber a 
	where	a.ie_situacao		= '1' 
	and	a.nr_interno_conta	= nr_interno_conta_p 
	
union
 
	SELECT	1 
	from	titulo_receber a 
	where	a.ie_situacao		= '1' 
	and	a.nr_seq_protocolo	= nr_seq_protocolo_w) alias2;
 
RETURN ie_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
