CREATE OR REPLACE FUNCTION obter_se_conta_titulo (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(1)	:= 'N';
qt_registros_w		bigint	:= 0;
nr_seq_conta_origem_w  conta_paciente.nr_seq_conta_origem%type;	
ie_cancelamento_w 	conta_paciente.ie_cancelamento%type;

BEGIN
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then 
	 
	select	count(*) 
	into STRICT	qt_registros_w 
	from	titulo_receber 
	where	nr_interno_conta	= nr_interno_conta_p;
	 
	if (qt_registros_w		> 0) then 
		ds_retorno_w		:= 'S';		
	end if;
	 
	if (qt_registros_w = 0) then 
		 
		begin 
			select 	ie_cancelamento, 
				nr_seq_conta_origem 
			into STRICT 	ie_cancelamento_w, 
				nr_seq_conta_origem_w 
			from 	conta_paciente 
			where 	nr_interno_conta = nr_interno_conta_p;	
		exception 
			when others then 
			ie_cancelamento_w:='';
			nr_seq_conta_origem_w:=0;
		end;
		 
		if (ie_cancelamento_w = 'E') and (nr_seq_conta_origem_w > 0) then 
		 
			select	count(*) 
			into STRICT	qt_registros_w 
			from	titulo_receber 
			where	nr_interno_conta	= nr_seq_conta_origem_w;
	 
			if (qt_registros_w		> 0) then 
		 
				ds_retorno_w := 'S';
		 
			end if;
	 
		end if;
	 
	 
	 
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
