CREATE OR REPLACE FUNCTION obter_se_contem_alertas (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_alerta_w	varchar(1);



BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	SELECT coalesce(MAX('S'), 'N')
	into STRICT ie_alerta_w
    from (
        SELECT 1
        FROM    atendimento_alerta     a,
                atendimento_paciente   b
        WHERE   b.cd_pessoa_fisica = cd_pessoa_fisica_p
            AND a.nr_atendimento = b.nr_atendimento
            AND a.ie_situacao = 'A'
            AND ((coalesce(a.dt_fim_alerta::text, '') = '')
                OR (a.dt_fim_alerta >= trunc(clock_timestamp(), 'dd')))
                and		((coalesce(a.dt_fim_alerta::text, '') = '') or (a.dt_fim_alerta >= trunc(clock_timestamp(),'dd')))
        
union

        SELECT 1
        FROM
            alerta_paciente
        WHERE
            cd_pessoa_fisica = cd_pessoa_fisica_p and ie_situacao = 'A'
            AND ((coalesce(dt_fim_alerta::text, '') = '')
                OR (dt_fim_alerta >= trunc(clock_timestamp(), 'dd')))
                and		((coalesce(dt_fim_alerta::text, '') = '') or (dt_fim_alerta >= trunc(clock_timestamp(),'dd')))
            ) alias27;

end if;

return ie_alerta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
