CREATE OR REPLACE FUNCTION obter_se_contem_classif ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, ds_lista_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

  item RECORD;

BEGIN

ds_retorno_w := 'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	for item in (	select  pc.nr_seq_classif nr_sequencia
			from    pessoa_classif pc
			where (pc.dt_inicio_vigencia <= clock_timestamp()
			and (coalesce(pc.dt_final_vigencia::text, '') = '' or pc.dt_final_vigencia >= clock_timestamp()))
			and     pc.cd_pessoa_fisica = cd_pessoa_fisica_p) loop

		if (coalesce(ds_retorno_w::text, '') = '' or ds_retorno_w = 'N') then

			select	obter_se_contido_char(item.nr_sequencia, ds_lista_p)
			into STRICT	ds_retorno_w
			;

		end if;
	
	end loop;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
