SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_contem_exame_pai ( cd_pessoa_fisica_p text) RETURNS timestamp AS $body$
DECLARE


nr_seq_exame_w		bigint;
nr_seq_transfusao_w	bigint;
nr_seq_exame_lote_w	bigint;
dt_realizado_w		timestamp;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_exame_w
	from	san_exame
	where	ie_tipo_exame	= 2;

	select	max(nr_sequencia)
	into STRICT	nr_seq_transfusao_w
	from	san_transfusao
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_exame_lote_w
	from	san_exame_lote
	where	nr_seq_transfusao	= nr_seq_transfusao_w;

	select	max(dt_realizado)
	into STRICT	dt_realizado_w
	from	san_exame_realizado
	where	nr_seq_exame	= nr_seq_exame_w
	and		Upper(ds_resultado)	= 'POSITIVO'
	and		nr_seq_exame_lote	= nr_seq_exame_lote_w;

end if;

return dt_realizado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
