SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_contem_funoes (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
cd_funcao_w		integer;
qt_funcao_w 		integer := 0;



C01 CURSOR FOR
	SELECT	cd_funcao
	from	mkt_produto a,
		mkt_prod_core_funcao b
	where	a.nr_sequencia = nr_sequencia_p
	and	a.nr_sequencia = b.nr_seq_produto;


BEGIN

open c01;
loop
fetch c01 into
	cd_funcao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

if (ie_retorno_w = 'S') then

	select	count(*)
	into STRICT	qt_funcao_w
	from	mkt_modulo_funcao
	where	nr_seq_area_modulo in (SELECT	nr_sequencia
					from	mkt_area_modulo
					where	nr_seq_prod_area in (select	nr_sequencia
									from	mkt_produto_area
									where	nr_seq_produto = nr_sequencia_p))
	and cd_funcao = cd_funcao_w;

	if (qt_funcao_w = 0) then
		ie_retorno_w := 'N';
	end if;

end if;

end loop;
close c01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
