CREATE OR REPLACE FUNCTION obter_se_contem_regra_semana ( dt_data_p timestamp, dt_semana_p bigint, ie_semana_p bigint) RETURNS varchar AS $body$
DECLARE

				
ie_retorno_w		varchar(1) := 'N';
dt_dia_w 		timestamp 	:= establishment_timezone_utils.startOfMonth(dt_data_p);
ie_dia_sem_w		varchar(10);
ie_dia_semana_data_w	varchar(10);
ie_semana_w		smallint:= 0;


BEGIN

ie_dia_semana_data_w:= obter_cod_dia_semana(dt_data_p);

while(dt_dia_w <= establishment_timezone_utils.startofday(dt_data_p)) loop

	ie_dia_sem_w:= obter_cod_dia_semana(dt_dia_w);
	if (ie_dia_sem_w = ie_dia_semana_data_w)then
		ie_semana_w:= ie_semana_w + 1;
	end if;
	dt_dia_w:= establishment_timezone_utils.startofday(dt_dia_w) +1;
	
end loop;

if (ie_semana_w = ie_semana_p and (ie_dia_sem_w = dt_semana_p OR (dt_semana_p = 9 and pkg_i18n.get_user_locale = 'de_AT')))then
	ie_retorno_w:= 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
