CREATE OR REPLACE FUNCTION obter_se_contido_entre_virgula (ds_lista_valores_p text, ds_valor_p text) RETURNS varchar AS $body$
DECLARE


ds_valores_w		varchar(255);
qt_length_inicial_w	integer;


BEGIN
ds_valores_w :=	',' || ds_lista_valores_p || ',';
qt_length_inicial_w := length(ds_valores_w);
ds_valores_w := replace(ds_valores_w, ',' || ds_valor_p || ',', '');

if (coalesce(length(ds_valores_w),0 )< qt_length_inicial_w) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
