SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_contrato_doc_atrasado ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ie_vencido_w	varchar(1);
ds_regra_w		varchar(80);
ie_regra_dia_w	varchar(1);
qt_dia_mes_w	smallint;
dt_especifica_w	timestamp;
dt_limite_w		timestamp;


c01 CURSOR FOR
SELECT	a.ds_regra,
	a.ie_regra_dia,
	a.qt_dia_mes,
	a.dt_especifica
from	contrato_regra_doc a
where	a.nr_seq_contrato = nr_sequencia_p
and not exists (
	SELECT	1
	from	contrato_controle_doc b
	where	b.nr_seq_regra = a.nr_sequencia);


BEGIN
ie_vencido_w	:= 'N';


OPEN C01;
LOOP
FETCH C01 into
	ds_regra_w,
	ie_regra_dia_w,
	qt_dia_mes_w,
	dt_especifica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	begin
	if (ie_regra_dia_w = 'M') then
		dt_limite_w	:= pkg_date_utils.get_date(pkg_date_utils.extract_field('YEAR', clock_timestamp(), 0), qt_dia_mes_w, 1, 0);
	elsif (ie_regra_dia_w = 'S') then
		dt_limite_w	:= PKG_DATE_UTILS.ADD_MONTH(pkg_date_utils.get_date(pkg_date_utils.extract_field('YEAR', clock_timestamp(), 0), qt_dia_mes_w, 1, 0), 1, 0);
	elsif (ie_regra_dia_w = 'U') then
		dt_limite_w	:= PKG_DATE_UTILS.end_of(pkg_date_utils.get_date(pkg_date_utils.extract_field('YEAR', clock_timestamp(), 0), qt_dia_mes_w, 1, 0), 'MONTH', 0);
	elsif (ie_regra_dia_w = 'D') then
		dt_limite_w	:= dt_especifica_w;
	end if;
	if (dt_limite_w < clock_timestamp()) then
		ie_vencido_w	:= 'S';
	end if;
	exception
		when others then
			ie_vencido_w	:= 'N';
	end;

	end;
end loop;
close c01;

return ie_vencido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
