CREATE OR REPLACE FUNCTION obter_se_contrato_em_reajuste ( nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	contrato a,
	contrato_regra_pagto b
where	a.nr_sequencia = nr_seq_contrato_p
and	a.nr_sequencia = b.nr_seq_contrato
and	a.ie_situacao = 'A'
and	coalesce(qt_aviso_reajuste,0) > 0
and	trunc(trunc(dt_inicio_vigencia,'dd') - trunc(clock_timestamp(),'dd')) <= coalesce(qt_aviso_reajuste,0);

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
