CREATE OR REPLACE FUNCTION obter_se_contrato_em_revisao (nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	contrato a
where	a.nr_sequencia = nr_seq_contrato_p
and	a.ie_situacao = 'A'
and	(a.dt_revisao IS NOT NULL AND a.dt_revisao::text <> '')
and	coalesce(a.qt_dias_revisao,0) > 0
and	trunc(trunc(a.dt_revisao,'dd') + coalesce(a.qt_dias_revisao,0)) <= trunc(clock_timestamp(),'dd');

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
