CREATE OR REPLACE FUNCTION obter_se_contrato_vencido (nr_seq_contrato bigint) RETURNS varchar AS $body$
DECLARE


qt_registros_w		integer;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	contrato
where	nr_sequencia = nr_seq_contrato
and	clock_timestamp() > coalesce(dt_fim,clock_timestamp());

if (qt_registros_w > 0) then
	return	'S'; /* Vencido */
else
	return	'N'; /* NÃ£o vencido */
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
