CREATE OR REPLACE FUNCTION obter_se_controla_leitos ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE




ie_retorno_w			varchar(2) := 'N';
qt_existe_w			integer;

BEGIN


select	coalesce(max(ie_mostra_gestao_disp),'N')
into STRICT	ie_retorno_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p;

if (ie_retorno_w = 'S') then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	unidade_atendimento
	where	ie_mostra_gestao_disp = 'S'
	and	cd_setor_atendimento = cd_setor_atendimento_p;

	if (qt_existe_w > 0) then
		begin
		select	coalesce(max(ie_mostra_gestao_disp),'N')
		into STRICT	ie_retorno_w
		from	unidade_atendimento
		where	cd_setor_atendimento 	= 	cd_setor_atendimento_p
		and	cd_unidade_basica	=	cd_unidade_basica_p
		and	cd_unidade_compl	=	cd_unidade_compl_p;
		end;
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
