CREATE OR REPLACE FUNCTION obter_se_controle_aberto (nr_seq_controle_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_controle_w		bigint  := 0;


BEGIN

if (nr_seq_controle_p 	> 0) and (nr_atendimento_p	> 0) then

	select 	count(*)
	into STRICT	qt_controle_w
	from	cih_controle_atend
	where	nr_seq_controle = nr_seq_controle_p
	and	nr_atendimento 	= nr_atendimento_p
	and	coalesce(dt_finalizacao::text, '') = '';

end if;


if (qt_controle_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
