CREATE OR REPLACE FUNCTION obter_se_controle_terceiro ( nr_seq_reserva_pf_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(1);


BEGIN

if (nr_seq_reserva_pf_p IS NOT NULL AND nr_seq_reserva_pf_p::text <> '') then
	begin
--Verifica se NR_SEQ_TERCEIRO_OPERACAO Ã© null, se sim verifica se existe sequencia na TERCEIRO_OPERACAO.
	select 	CASE WHEN coalesce(a.nr_seq_terceiro_operacao::text, '') = '' THEN 			CASE WHEN(select	max(b.nr_sequencia)				from	terceiro_operacao b				where	b.nr_seq_reserva_pf = a.nr_sequencia) IS NULL THEN 'N'  ELSE 'S' END   ELSE 'S' END
	into STRICT	retorno_w
	from  nut_reserva_pf	a
	where nr_sequencia = nr_seq_reserva_pf_p;

	end;
	end if;

return	retorno_w;

end;
$body$
