CREATE OR REPLACE FUNCTION obter_se_convenio_ativo ( cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ativo_w	varchar(1);


BEGIN

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (cd_convenio_p <> 0)then
	select	coalesce(ie_situacao,'I')
	into STRICT	ie_ativo_w
	from	convenio
	where	cd_convenio = cd_convenio_p;
end if;

if (cd_convenio_p = 0) then
	ie_ativo_w	:= 'A';
end if;

return	ie_ativo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
