CREATE OR REPLACE FUNCTION obter_se_convenio_cancelado (cd_convenio_p bigint, dt_base_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_reg_w	bigint;


BEGIN

select 	coalesce(max(1), 0)
into STRICT	qt_reg_w
from 	convenio
where 	cd_convenio = cd_convenio_p
and 	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '')
and 	trunc(dt_cancelamento,'dd') <= trunc(coalesce(dt_base_p,clock_timestamp()),'dd');

ds_retorno_w:= 'N';
if (qt_reg_w > 0) then
	ds_retorno_w:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
