CREATE OR REPLACE FUNCTION obter_se_convenio_episodio_atx (cd_convenio_p bigint, nr_seq_episodio_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
BEGIN

begin
select	'S'
into STRICT	ds_retorno_w
from	atendimento_paciente x,
	atend_categoria_convenio y
where	x.nr_atendimento 	= y.nr_atendimento
and	coalesce(x.dt_cancelamento::text, '') = ''
and	x.nr_seq_episodio 	= nr_seq_episodio_p
and	x.nr_atendimento	<> nr_atendimento_p
and	y.cd_convenio 		= cd_convenio_p  LIMIT 1;
exception
when others then
	ds_retorno_w := 'N';
end;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
