CREATE OR REPLACE FUNCTION obter_se_convenio_material ( cd_convenio_p bigint, cd_material_p bigint, ie_tipo_Atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1) := 'S';
cont_w				bigint;

c01 CURSOR FOR
SELECT	/*+ index(a REPRCM_PK)*/		coalesce(ie_permite,'S')
from	rep_regra_convenio_mat a
where	coalesce(cd_convenio,cd_convenio_p) = cd_convenio_p
and		cd_material = cd_material_p
and		coalesce(ie_tipo_Atendimento,coalesce(ie_tipo_Atendimento_p,0)) = coalesce(ie_tipo_Atendimento_p,0)
and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
order by coalesce(cd_convenio,0),
		coalesce(ie_tipo_Atendimento,0);

BEGIN
/*
select	count(*)
into	cont_w
from	rep_regra_convenio_mat
where	cd_material = cd_material_p;*/
SELECT /*+ index(a REPRCM_PK)*/		count(*)
into STRICT	cont_w
FROM	rep_regra_convenio_mat a
 where a.cd_material = cd_material_p;

if (cont_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_permite_w	:= ie_permite_w;
	end loop;
	close C01;
end if;

return	ie_permite_w;

end;
$body$
