CREATE OR REPLACE FUNCTION obter_se_convenio_publico (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tem_conv_publico_w	varchar(1) := 'N';
cd_convenio_w	numeric(20);
const_convenio_privado_w smallint := 1;

C01 CURSOR FOR
	SELECT	coalesce(cd_convenio, 0) cd_convenio
	from	Atend_categoria_convenio a
	where	a.nr_atendimento = nr_atendimento_p
	and	a.dt_inicio_vigencia	=
		(SELECT	max(dt_inicio_vigencia)
		from 	Atend_categoria_convenio b
		where 	b.nr_atendimento	= nr_atendimento_p);

BEGIN

	open C01;
	loop
	fetch C01 into
		cd_convenio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		if (obter_tipo_convenio(cd_convenio_w) <> const_convenio_privado_w) then
			ie_tem_conv_publico_w := 'S';
		end if;
	end;
	end loop;
	close C01;
return ie_tem_conv_publico_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
