CREATE OR REPLACE FUNCTION obter_se_converte_ml ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
cd_unidade_medida_consumo_w	varchar(30);
cd_unidade_medida_w		varchar(30);
cd_estabelecimento_w		smallint := wheb_usuario_pck.get_cd_estabelecimento;

c01 CURSOR FOR
	SELECT	upper(cd_unidade_medida)
	from	material_conversao_unidade
	where	cd_material	= cd_material_p;

BEGIN

select	upper(substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_w,'UMS'),1,30))
into STRICT	cd_unidade_medida_consumo_w
from	unidade_medida b,
	material a
where	a.cd_material = cd_material_p
and	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_w,'UMS'),1,30) = b.cd_unidade_medida;

if	upper(cd_unidade_medida_consumo_w) = upper(obter_unid_med_usua('ML')) or
	upper(cd_unidade_medida_consumo_w) = upper(obter_unid_med_usua('MI')) then
	ds_retorno_w	:= 'S';
end if;

if (ds_retorno_w <> 'S') then
	OPEN C01;
	LOOP
	FETCH C01 into
		cd_unidade_medida_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	if (upper(cd_unidade_medida_w) = upper(obter_unid_med_usua('ML'))) or (upper(cd_unidade_medida_w) = upper(obter_unid_med_usua('MI'))) then
		ds_retorno_w	:= 'S';
	end if;
	END LOOP;
	CLOSE C01;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
