SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_convret_repasse ( nr_seq_ret_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
C - Com repasse
G - Com repasse nos itens glosados
*/
ds_retorno_w		varchar(5);
qt_retorno_w		bigint;


BEGIN

ds_retorno_w		:='N';
qt_retorno_w		:= 0;

if (ie_opcao_p = 'C') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	procedimento_repasse b,
		procedimento_paciente a,
		convenio_retorno_item c
	where	a.nr_sequencia		= b.nr_seq_procedimento
	and	a.nr_interno_conta	= c.nr_interno_conta
	and	c.nr_sequencia		= nr_seq_ret_item_p;

	if (qt_retorno_w = 0) then
		select	count(*)
		into STRICT	qt_retorno_w
		from	material_repasse b,
			material_atend_paciente a,
			convenio_retorno_item c
		where	a.nr_sequencia		= b.nr_seq_material
		and	a.nr_interno_conta	= c.nr_interno_conta
		and	c.nr_sequencia		= nr_seq_ret_item_p;

	end if;
end if;

if (ie_opcao_p = 'G') then

	select	count(*)
	into STRICT	qt_retorno_w
	from	procedimento_repasse b,
		convenio_retorno_glosa a,
		convenio_retorno_item c
	where	a.nr_sequencia		= b.nr_seq_ret_glosa
	and	a.nr_seq_ret_item	= c.nr_sequencia
	and	c.nr_sequencia		= nr_seq_ret_item_p;

	if (qt_retorno_w = 0) then
		select	count(*)
		into STRICT	qt_retorno_w
		from	material_repasse b,
			convenio_retorno_glosa a,
			convenio_retorno_item c
		where	a.nr_sequencia		= b.nr_seq_ret_glosa
		and	a.nr_seq_ret_item	= c.nr_sequencia
		and	c.nr_sequencia		= nr_seq_ret_item_p;
	end if;

end if;

if (ie_opcao_p = 'S') then

	select	count(*)
	into STRICT	qt_retorno_w
	from	procedimento_repasse b,
		procedimento_paciente a,
		convenio_retorno_item c
	where	a.nr_sequencia		= b.nr_seq_procedimento
	and	a.nr_interno_conta	= c.nr_interno_conta
	and	c.nr_sequencia		= nr_seq_ret_item_p;

	if (qt_retorno_w = 0) then
		select	count(*)
		into STRICT	qt_retorno_w
		from	material_repasse b,
			material_atend_paciente a,
			convenio_retorno_item c
		where	a.nr_sequencia		= b.nr_seq_material
		and	a.nr_interno_conta	= c.nr_interno_conta
		and	c.nr_sequencia		= nr_seq_ret_item_p;
	end if;

	if (qt_retorno_w = 0) then

		select	count(*)
		into STRICT	qt_retorno_w
		from	convenio_retorno_glosa a,
			convenio_retorno_item c
		where	a.nr_seq_ret_item	= c.nr_sequencia
		and	c.nr_sequencia		= nr_seq_ret_item_p;

		if (qt_retorno_w = 0) then
			select	count(*)
			into STRICT	qt_retorno_w
			from	convenio_retorno_glosa a,
				convenio_retorno_item c
			where	a.nr_seq_ret_item	= c.nr_sequencia
			and	c.nr_sequencia		= nr_seq_ret_item_p;
		end if;
	else
		qt_retorno_w := 0;
	end if;

end if;

if (qt_retorno_w	> 0) then
	ds_retorno_w		:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
