SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_convret_rep_lib (nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_ie_status_repasse_w	bigint;
ds_retorno_w		varchar(1);


BEGIN 
 
select	count(*) 
into STRICT	qt_ie_status_repasse_w 
from	procedimento_repasse c, 
	procedimento_paciente b, 
	conta_paciente d, 
	convenio_retorno_item a 
where	a.nr_sequencia		= nr_seq_item_p 
and	a.nr_interno_conta	= d.nr_interno_conta 
and	d.nr_interno_conta	= b.nr_interno_conta 
and	b.nr_sequencia		= c.nr_seq_procedimento 
and	c.ie_status		in ('R','S','L') 
and (b.nr_doc_convenio = a.cd_autorizacao or coalesce(b.nr_doc_convenio::text, '') = '');
 
ds_retorno_w	:= 'N';
 
if	qt_ie_status_repasse_w > 0 then 
	ds_retorno_w	:= 'S';
else 
	select	count(*) 
	into STRICT	qt_ie_status_repasse_w 
	from	material_repasse c, 
		material_atend_paciente b, 
		conta_paciente d, 
		convenio_retorno_item a 
	where	a.nr_sequencia	= nr_seq_item_p 
	and	a.nr_interno_conta = d.nr_interno_conta 
	and	d.nr_interno_conta	= b.nr_interno_conta 
	and	b.nr_sequencia	= c.nr_seq_material 
	and	c.ie_status		in ('R','S','L') 
	and (b.nr_doc_convenio = a.cd_autorizacao or coalesce(b.nr_doc_convenio::text, '') = '');
 
	if	qt_ie_status_repasse_w > 0 then 
		ds_retorno_w	:= 'S';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
