CREATE OR REPLACE FUNCTION obter_se_conv_atend_vig (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select 	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	atend_categoria_convenio
	where	nr_atendimento = nr_atendimento_p
	and   	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_final_vigencia,clock_timestamp() + interval '1 days');

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
