SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_conv_doc_lib ( cd_convenio_p bigint, ie_tipo_atendimento_p bigint, nr_seq_classificacao_p bigint, cd_plano_p text) RETURNS varchar AS $body$
DECLARE



cd_plano_w		varchar(10)	:= coalesce(cd_plano_p,'XPTO');
qt_reg_w			bigint;
qt_observacao_w		bigint;
ds_retorno_w		varchar(1);
cd_estabelecimento_w	convenio_doc_atend.cd_estabelecimento%type;


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

if (obter_valor_param_usuario(916, 602, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w) = 'S') then
	select	count(*)
	into STRICT	qt_observacao_w
	from	convenio
	where	cd_convenio = cd_convenio_p
	and	(ds_observacao IS NOT NULL AND ds_observacao::text <> '');

	if (qt_observacao_w > 0) then
		select	count(*)
		into STRICT	qt_reg_w
		from	convenio_doc_atend a
		where	cd_convenio	= cd_convenio_p
		and	coalesce(cd_plano,cd_plano_w)	= cd_plano_w
		and	a.cd_estabelecimento = coalesce(cd_estabelecimento_w,a.cd_estabelecimento)
		and	obter_se_doc_convenio(a.nr_sequencia,ie_tipo_atendimento_p,obter_perfil_ativo,nr_seq_classificacao_p)	= 'S';
	end if;

	if (qt_reg_w > 0) and (qt_observacao_w > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
else
	begin
	select	count(*)
	into STRICT	qt_observacao_w
	from	convenio
	where	cd_convenio = cd_convenio_p
	and	(ds_observacao IS NOT NULL AND ds_observacao::text <> '');

	select	count(*)
	into STRICT	qt_reg_w
	from	convenio_doc_atend a
	where	cd_convenio	= cd_convenio_p
	and	coalesce(cd_plano,cd_plano_w)	= cd_plano_w
	and	a.cd_estabelecimento = coalesce(cd_estabelecimento_w,a.cd_estabelecimento)
	and	obter_se_doc_convenio(a.nr_sequencia,ie_tipo_atendimento_p,obter_perfil_ativo,nr_seq_classificacao_p)	= 'S';

	if (qt_reg_w = 0) and (qt_observacao_w = 0) then
		return 'N';
	else
		return	'S';
	end if;
	end;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
