CREATE OR REPLACE FUNCTION obter_se_conv_lib_conta_banco (nr_seq_conta_banco_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE



ie_visualiza_w	varchar(5) := 'S';
qt_regra_w	bigint;

BEGIN
ie_visualiza_w	:= 'S';
select	count(*)
into STRICT	qt_regra_w
from	banco_estab_convenio
where	cd_convenio = cd_convenio_p;

if (qt_regra_w > 0) then
	begin
	select	'S'
	into STRICT	ie_visualiza_w
	from	banco_estab_convenio
	where	nr_seq_conta_banco = nr_seq_conta_banco_p
	and	cd_convenio	   = cd_convenio_p  LIMIT 1;
	exception
	when others then
		ie_visualiza_w := 'N';
	end;

end if;

return	ie_visualiza_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
