CREATE OR REPLACE FUNCTION obter_se_conv_lib_func_ag ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_funcao_p bigint, cd_perfil_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1);
ds_Retorno_w		varchar(01)	:= 'S';
qt_regra_w		bigint;
cd_perfil_w		bigint;
qt_registro_w		bigint;
ie_considera_tab_w 	varchar(1);
dt_referencia_w		timestamp;

C01 CURSOR FOR
	SELECT	coalesce(a.ie_permite_convenio,'S')
	FROM	CONVEIO_ESTAB_FUNC_LIB a
	WHERE	a.cd_estabelecimento		= cd_estabelecimento_p
	AND	a.cd_funcao			= cd_funcao_p
	and	a.cd_convenio			= cd_convenio_p
	AND (coalesce(a.dt_inicio_regra,dt_referencia_w)		<= dt_referencia_w)
	and	coalesce(a.cd_perfil, cd_perfil_p)	= cd_perfil_p
	order by coalesce(a.cd_perfil,999);


BEGIN

dt_referencia_w	:= coalesce(dt_referencia_p,clock_timestamp());

SELECT	COUNT(*)
INTO STRICT	qt_regra_w
FROM	CONVEIO_ESTAB_FUNC_LIB a
WHERE	a.cd_estabelecimento		= cd_estabelecimento_p
AND	a.cd_funcao			= cd_funcao_p
and	a.cd_convenio			= cd_convenio_p
AND (coalesce(a.dt_inicio_regra,dt_referencia_w)		<= dt_referencia_w);

if (qt_regra_w	> 0) then
	open C01;
	loop
	fetch C01 into
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w	:= ie_permite_w;
		end;
	end loop;
	close C01;
end if;

--AND	NVL(a.IE_PERMITE_CONVENIO,'S')	= 'N'
return	ds_retorno_W;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
