CREATE OR REPLACE FUNCTION obter_se_conv_lib_titular ( cd_pessoa_fisica_p text, cd_convenio_p bigint, dt_agenda_p timestamp ) RETURNS varchar AS $body$
DECLARE

ie_retorno_w		varchar(1) := 'S';
ie_exige_convenio_w     varchar(1);
qt_reg_w		bigint;


BEGIN

	select coalesce(MAX(ie_exige_titular),'N')
	  into STRICT ie_exige_convenio_w
	  from convenio
	 where cd_convenio = cd_convenio_p;

	if (ie_exige_convenio_w = 'S') then

		ie_retorno_w := 'N';

        select	count(*)
		  into STRICT	qt_reg_w
		  from	pessoa_titular_convenio
		 where  cd_pessoa_fisica = cd_pessoa_fisica_p
		   and 	cd_convenio = cd_convenio_p
		   and  coalesce(dt_inicio_vigencia, trunc(dt_agenda_p)) <= trunc(dt_agenda_p) and coalesce(dt_fim_vigencia, trunc(dt_agenda_p)) >= trunc(dt_agenda_p);

		if (qt_reg_w > 0) then
			ie_retorno_w := 'S';
		end if;
	end if;

	return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
