CREATE OR REPLACE FUNCTION obter_se_conv_rec_alerta (cd_convenio_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_recebe_w	varchar(1) := 'S';
qt_existe_regra_w	bigint;
nr_sequencia_w		bigint;


BEGIN

ie_recebe_w := 'S';

	select	count(*)
	into STRICT	qt_existe_regra_w
	from	regra_envio_sms_convenio
	where	nr_seq_regra = nr_seq_regra_p;

if (qt_existe_regra_w = 0) then
	ie_recebe_w := 	'S';
else

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	regra_envio_sms_convenio
	where (coalesce(cd_convenio,cd_convenio_p) = cd_convenio_p)
	and	nr_seq_regra = nr_seq_regra_p;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

		select	coalesce(max(ie_receber_alerta),'S')
		into STRICT	ie_recebe_w
		from	regra_envio_sms_convenio
		where	nr_sequencia = nr_sequencia_w;
	else
		ie_recebe_w := 'N';
	end if;

end if;

return	ie_recebe_w;

end;
$body$
