CREATE OR REPLACE FUNCTION obter_se_cosns_setor_unid_conv (cd_setor_atendimento_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_sequencia_w			bigint;
ie_atende_w				varchar(1)	:= 'S';
cd_setor_atendimento_w	integer;
cd_convenio_w			integer;
ie_tipo_convenio_w		smallint;
ie_possui_regra_w		varchar(1);
cd_setor_usuario_w	integer;	
cd_perfil_logado_w		bigint;			
/*Existe a function obter_setor_unid_conv, para considerar a unidade da regra*/
 
 
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_atende 
	from	setor_unid_conv 
	where	coalesce(cd_convenio, cd_convenio_w) 				= cd_convenio_w 
	and	coalesce(cd_setor_atendimento, cd_setor_atendimento_p) 	= cd_setor_atendimento_p 
	and	coalesce(cd_perfil, cd_perfil_logado_w)					= cd_perfil_logado_w 
	and	coalesce(ie_tipo_convenio, ie_tipo_convenio_w) 			= ie_tipo_convenio_w 
	and	coalesce(ie_consiste_setor,'N') 							= 'S' 
	and	coalesce(cd_setor_usuario, cd_setor_usuario_w) 			= cd_setor_usuario_w;


BEGIN	 
 
cd_perfil_logado_w := wheb_usuario_pck.get_cd_perfil;
begin 
	select 	'S' 
	into STRICT	ie_possui_regra_w 
	from	setor_unid_conv 
	where	coalesce(cd_perfil, cd_perfil_logado_w)	=	cd_perfil_logado_w 
	and		coalesce(ie_consiste_setor,'N') = 'S'  LIMIT 1;
exception 
when others then 
	ie_possui_regra_w := 'N';
end;
 
if (ie_possui_regra_w = 'S')	then 
	cd_setor_usuario_w := wheb_usuario_pck.get_cd_setor_atendimento;
	cd_convenio_w	:= obter_dados_atendimento(nr_atendimento_p, 'CC');
 
	begin 
		select	ie_tipo_convenio 
		into STRICT	ie_tipo_convenio_w 
		from	convenio 
		where	cd_convenio = cd_convenio_w  LIMIT 1;
	exception 
	when others then 
		ie_tipo_convenio_w := 0;
	end;
 
	open C01;
	loop 
	fetch C01 into	 
		nr_sequencia_w, 
		ie_atende_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		ie_atende_w	:=	ie_atende_w;	
		end;
	end loop;
	close C01;
end if;	
 
 
return	ie_atende_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
