CREATE OR REPLACE FUNCTION obter_se_cotacao_aprov ( dt_aprovacao_p timestamp, nr_cot_compra_p bigint, IE_FINALIDADE_COTACAO_p text default null) RETURNS varchar AS $body$
DECLARE


ie_status_w		varchar(15);
dt_aprovacao_w		timestamp;
qt_registros_w		bigint;


BEGIN


if (dt_aprovacao_p IS NOT NULL AND dt_aprovacao_p::text <> '') then
	return 'S';
end if;


if (IE_FINALIDADE_COTACAO_p	= 'F') then
	return 'S';
end if;


select	count(*)
into STRICT	qt_registros_w
from	cot_compra_item
where	nr_cot_compra = nr_cot_compra_p
and	(nr_seq_aprovacao IS NOT NULL AND nr_seq_aprovacao::text <> '');

if (qt_registros_w > 0) then
	return 'S';
end if;

return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
