CREATE OR REPLACE FUNCTION obter_se_cotacao_moeda (cd_moeda_p bigint, vl_cotacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_p	varchar(1) := 'S';
vl_cotacao_w	cotacao_moeda.vl_cotacao%type;
tx_cotacao_w	double precision;
qt_regra_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_cotacao_moeda a
where	a.cd_moeda	= cd_moeda_p;

if (coalesce(qt_regra_w,0)	> 0) then

	begin

		select (100 * coalesce(vl_cotacao_p,0)) / coalesce(b.vl_cotacao,0) tx_cotacao,
			b.vl_cotacao
		into STRICT	tx_cotacao_w,
			vl_cotacao_w
		from	cotacao_moeda b,
			moeda a
		where	dt_cotacao = (	SELECT	max(y.dt_cotacao)
					from	cotacao_moeda y,
						moeda x
					where	y.cd_moeda = x.cd_moeda
					and	x.cd_moeda = cd_moeda_p)
		and	b.cd_moeda = a.cd_moeda
		and	a.cd_moeda = cd_moeda_p;

		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_p
		from	regra_cotacao_moeda
		where	cd_moeda = cd_moeda_p
		and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_final_vigencia,clock_timestamp())
		and	vl_cotacao_p between coalesce(vl_cotacao_w + vl_inferior_maximo,vl_cotacao_p) and coalesce(vl_cotacao_w + vl_superior_maximo,vl_cotacao_p)
		and	tx_cotacao_w between coalesce(tx_inferior_maxima,tx_cotacao_w) and coalesce(tx_superior_maxima,tx_cotacao_w);

	/* se a taxa de cotação for muito alta (superior a 99999999999,9999), vai cair no exception e vai consistir */

	exception
	when others then

		ds_retorno_p	:= 'N';

	end;

end if;

return	ds_retorno_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
