CREATE OR REPLACE FUNCTION obter_se_cotacao_preco_igual ( nr_seq_cot_item_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_prioridade_w		integer;
vl_vencedor_w			double precision;
vl_item_w			double precision;
nr_seq_item_fornec_venc_w		bigint;
qt_iguais_w			integer;
nr_cot_compra_w		bigint;
nr_item_cot_compra_w		integer;


BEGIN 
qt_iguais_w			:= 0;
 
select	nr_cot_compra, 
	nr_item_cot_compra 
into STRICT	nr_cot_compra_w, 
	nr_item_cot_compra_w 
from	cot_compra_forn_item 
where	nr_sequencia = nr_seq_cot_item_p;
 
select	coalesce(min(vl_presente),0) 
into STRICT	vl_item_w 
from	cot_compra_forn_item_tr_v 
where	nr_seq_item_fornec	= nr_seq_cot_item_p;
 
select	/*Obter_fornecedor_venc_cotacao(nr_cot_compra_w, nr_item_cot_compra_w)*/
 
	Obter_Venc_Cot_Fornec_item(nr_cot_compra_w, nr_item_cot_compra_w) 
into STRICT	nr_seq_item_fornec_venc_w
;
 
select	coalesce(min(vl_presente),0), 
	coalesce(min(qt_prioridade),0) 
into STRICT	vl_vencedor_w, 
	qt_prioridade_w 
from	cot_compra_forn_item_tr_v 
where	nr_cot_compra 		= nr_cot_compra_w 
and	nr_item_cot_compra 	= nr_item_cot_compra_w 
and	nr_seq_item_fornec	= nr_seq_item_fornec_venc_w;
 
if (vl_vencedor_w	= vl_item_w) then 
	select	count(*) 
	into STRICT	qt_iguais_w 
	from	cot_compra_forn_item_tr_v 
	where	nr_cot_compra 		= nr_cot_compra_w 
	and	nr_item_cot_compra 	= nr_item_cot_compra_w 
	and	vl_presente		= vl_vencedor_w 
	and	qt_prioridade		= qt_prioridade_w 
	and	nr_seq_item_fornec	<> nr_seq_item_fornec_venc_w;
end if;
 
return	qt_iguais_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
