CREATE OR REPLACE FUNCTION obter_se_cot_item_agrupado ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w				bigint;
ie_agrupado_w				varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cot_compra_solic_agrup
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_cot_compra_p;

if (qt_existe_w > 0) then
	ie_agrupado_w := 'S';
end if;

return ie_agrupado_w;

end;
$body$
