CREATE OR REPLACE FUNCTION obter_se_cpf_cgc_estrangeiro ( cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno				varchar(2);
ie_brasileiro_w				nacionalidade.ie_brasileiro%type;
nr_seq_pais_estab_w			pais.nr_sequencia%type;
nr_seq_pais_cgc_w			pais.nr_sequencia%type;


BEGIN
ds_retorno:=	'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select 	coalesce(max(b.ie_brasileiro),'N')
	into STRICT	ie_brasileiro_w
	from   	pessoa_fisica a,
			nacionalidade b
	where  	b.cd_nacionalidade = a.cd_nacionalidade
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	if (ie_brasileiro_w = 'N') then
		ds_retorno:= 'S';
	end if;
	end;
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	select 	coalesce(max(c.nr_sequencia),0)
	into STRICT	nr_seq_pais_estab_w
	from	estabelecimento a,
		pessoa_juridica b,
		pais c
	where	b.cd_cgc		= a.cd_cgc
	and 	c.nr_sequencia		= b.nr_seq_pais
	and	a.cd_estabelecimento	= cd_estabelecimento_p;

	select 	coalesce(max(c.nr_sequencia),0)
	into STRICT	nr_seq_pais_cgc_w
	from	pessoa_juridica b,
		pais c
	where	c.nr_sequencia	= b.nr_seq_pais
	and 	b.cd_cgc 	= cd_cgc_p;

	if (nr_seq_pais_estab_w <> 0) and (nr_seq_pais_cgc_w <> 0) and (nr_seq_pais_estab_w <> nr_seq_pais_cgc_w) then
		ds_retorno:= 'S';
	end if;

	end;
end if;

return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
