SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_cpoe_liberada (nr_sequencia_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE

 
ie_liberado_w		varchar(1);
			

BEGIN 
 
	if (ie_tipo_item_p in ('M','MAT','SOL')) then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_liberado_w 
		from	cpoe_material 
		where	nr_sequencia	= nr_sequencia_p 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (ie_tipo_item_p = 'P') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_liberado_w 
		from	cpoe_procedimento 
		where	nr_sequencia	= nr_sequencia_p 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (ie_tipo_item_p in ('D','SNE','S','LD')) then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_liberado_w 
		from	cpoe_dieta 
		where	nr_sequencia	= nr_sequencia_p 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (ie_tipo_item_p = 'DI') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_liberado_w 
		from	cpoe_dialise 
		where	nr_sequencia	= nr_sequencia_p 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (ie_tipo_item_p = 'R') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_liberado_w 
		from	cpoe_recomendacao 
		where	nr_sequencia	= nr_sequencia_p 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (ie_tipo_item_p = 'O') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_liberado_w 
		from	cpoe_gasoterapia 
		where	nr_sequencia	= nr_sequencia_p 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	end if;	
 
return	ie_liberado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_cpoe_liberada (nr_sequencia_p bigint, ie_tipo_item_p text) FROM PUBLIC;

