CREATE OR REPLACE FUNCTION obter_se_credenciamento_venc (cd_estabelecimento_p bigint, cd_especialidade_p text, cd_paciente_p text, dt_referencia_p timestamp, nr_atendimento_p bigint, cd_pf_credenciada_p text) RETURNS varchar AS $body$
DECLARE


ie_pf_credenciada_w	varchar(2);
qt_credenciamento_w	bigint;


BEGIN
if (cd_pf_credenciada_p IS NOT NULL AND cd_pf_credenciada_p::text <> '') then

	/* obter se regras pf */

	select	count(*)
	into STRICT	qt_credenciamento_w
	from	pf_credenciamento
	where	cd_pf_credenciamento	= cd_pf_credenciada_p
	and	((cd_estabelecimento	= cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''));

	/* verificar regras pf venc */

	if (qt_credenciamento_w > 0) then

		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_pf_credenciada_w
		from	pf_credenciamento
		where	cd_pf_credenciamento				= cd_pf_credenciada_p
		and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
		and	coalesce(cd_especialidade,cd_especialidade_p)	= cd_especialidade_p
		and	coalesce(cd_paciente,cd_paciente_p)			= cd_paciente_p
		and	coalesce(nr_atendimento, nr_atendimento_p)		= nr_atendimento_p
		and	consistir_vigencia_credenc(dt_credenciamento,dt_fim_credenciamento,dt_referencia_p)	= 'N';

	else

		ie_pf_credenciada_w	:= 'NC';

	end if;

end if;

return ie_pf_credenciada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
