CREATE OR REPLACE FUNCTION obter_se_ctb_criterio (nr_seq_criterio_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


cont_w			bigint;
qt_regra_w		bigint;
ie_liberado_w		varchar(255) := 'S';


BEGIN

select	count(*)
into STRICT	qt_regra_w
from 	ctb_criterio_rateio a,
	ctb_crit_rateio_lib_cp b
where	a.nr_sequencia 		= b.nr_seq_criterio
and 	ie_situacao = 'A'
and (b.nr_seq_criterio	= nr_seq_criterio_p
or	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
or	b.cd_cgc		= cd_cgc_p);

if (qt_regra_w > 0) then
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select	count(*)
		into STRICT	cont_w
		from 	ctb_crit_rateio_lib_cp
		where	nr_seq_criterio		= nr_seq_criterio_p
		and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		select	count(*)
		into STRICT	cont_w
		from 	ctb_crit_rateio_lib_cp
		where	nr_seq_criterio	= nr_seq_criterio_p
		and	cd_cgc		= cd_cgc_p;
	end if;
end if;

if (cont_w = 0) then
	ie_liberado_w	:= 'N';
else
	ie_liberado_w	:= 'S';
end if;

return		ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
