CREATE OR REPLACE FUNCTION obter_se_culturas_vinculadas (cd_microorganismo_p bigint, nr_seq_prescr_p bigint, nr_prescricao_P bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w varchar(1);


BEGIN
if (coalesce(cd_microorganismo_p, 0) > 0) then

	SELECT 	CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END
 	into STRICT 	ie_retorno_w
	from  	cih_precaucao_cultura
	where 	cd_microorganismo = cd_microorganismo_p
	and		nr_seq_prescr = nr_seq_prescr_p
	and		nr_prescricao = nr_prescricao_p;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
