CREATE OR REPLACE FUNCTION obter_se_curativo_vencido (nr_seq_ferida_p bigint) RETURNS varchar AS $body$
DECLARE


ie_vencido_w		varchar(1) := 'N';
nr_seq_curativo_w	bigint;



BEGIN
if (nr_seq_ferida_p IS NOT NULL AND nr_seq_ferida_p::text <> '') then

	Select  max(nr_sequencia)
	into STRICT	nr_seq_curativo_w
	from 	cur_curativo
	where   nr_seq_ferida = nr_seq_ferida_p
	and		coalesce(dt_inativacao::text, '') = '';

	if (nr_seq_curativo_w IS NOT NULL AND nr_seq_curativo_w::text <> '') then


		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_vencido_w
		from   	cur_ferida a,
				cur_curativo b
		where  	coalesce(a.dt_alta_curativo::text, '') = ''
		and    	a.nr_sequencia = b.nr_seq_ferida
		and		coalesce(a.dt_inativacao::text, '') = ''
		and		a.nr_sequencia = nr_seq_ferida_p
		and		b.nr_horas_validade > 0
		and		coalesce(b.dt_inativacao::text, '') = ''
		and    	clock_timestamp() >= (b.dt_curativo + (b.nr_horas_validade/24))
		and    	b.nr_sequencia = nr_seq_curativo_w;

	end if;

end if;

return ie_vencido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_curativo_vencido (nr_seq_ferida_p bigint) FROM PUBLIC;

