SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_dados_problema ( nr_seq_problema_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';



BEGIN

if (nr_seq_problema_p IS NOT NULL AND nr_seq_problema_p::text <> '') then

	if (ie_opcao_p = 'P') then

		Select 	max(CASE WHEN coalesce(qt_registro,0)=0 THEN 'N'  ELSE 'S' END )
		into STRICT	ds_retorno_w
		from ( SELECT count(1) qt_registro
				from   prescr_item_sintoma
				where  nr_seq_problema = nr_seq_problema_p
				
union all

				SELECT count(1) qt_registro
				from   soap_item_sintoma
				where  nr_seq_problema = nr_seq_problema_p
				
union all

				Select count(1) qt_registro
				from   atend_encaminhamento_probl
				where  nr_seq_problema = nr_seq_problema_p) alias7;

	end if;


end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
