CREATE OR REPLACE FUNCTION obter_se_datas_coincidem ( primeira_data_p timestamp, primeira_duracao_p bigint, segunda_data_p timestamp, segunda_duracao_p bigint ) RETURNS varchar AS $body$
DECLARE


retorno_w varchar(1) := 'S';

BEGIN

if (primeira_data_p = segunda_data_p ) then
	retorno_w := 'S';
elsif (primeira_data_p >= segunda_data_p + (segunda_duracao_p/1440)) then
	retorno_w := 'N';
elsif (segunda_data_p >= primeira_data_p + (primeira_duracao_p/1440)) then
	retorno_w := 'N';
elsif (segunda_data_p +(segunda_duracao_p/1440) <= primeira_data_p) then
	retorno_w := 'N';
elsif (primeira_data_p + (primeira_duracao_p/1440) <= segunda_data_p) then
	retorno_w := 'N';
end if;


return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
