CREATE OR REPLACE FUNCTION obter_se_datas_entre_periodo ( dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1) := 'N';


BEGIN

if (dt_inicial_p	>= dt_periodo_inicial_p) and (dt_final_p	<= dt_periodo_final_p) then

	ie_retorno_w	:= 'S';

elsif (dt_inicial_p	<= dt_periodo_inicial_p) and (dt_final_p 	<= dt_periodo_final_p and dt_final_p >= dt_periodo_inicial_p) then

	ie_retorno_w	:= 'S';

elsif (dt_inicial_p	>= dt_periodo_inicial_p and dt_inicial_p <= dt_periodo_final_p) and (dt_final_p 	>= dt_periodo_final_p) then

	ie_retorno_w	:= 'S';

end if;

return ie_retorno_w;

end;
