SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_data_conta_alta ( nr_atendimento_p bigint, ie_opcao_p text DEFAULT NULL) RETURNS timestamp AS $body$
DECLARE

 
dt_retorno_w	timestamp;
dt_intermed_w	timestamp;
qt_contas_w	integer;
dt_alta_w		timestamp;
dt_tesouraria_w	timestamp;

/* Function criada para atender alterações realizadas na pasta Tempo de Status da Ocupação Hospitalar para o HMSL */
 
 

BEGIN 
 
	SELECT MAX(dt_conta_definitiva) 
	INTO STRICT dt_intermed_w 
	FROM conta_paciente 
	WHERE nr_atendimento = nr_atendimento_p;
 
	IF (dt_intermed_w IS NOT NULL AND dt_intermed_w::text <> '') THEN 
		dt_retorno_w := dt_intermed_w; /*houve conta particular fechada*/
	ELSE 
		SELECT MAX(dt_alta) 
		INTO STRICT dt_alta_w 
		FROM atendimento_paciente 
		WHERE nr_atendimento = nr_atendimento_p;
 
		dt_retorno_w := dt_alta_w;
	END IF;
 
	/* Opção R criada para atender o relatório status de leito do São Luiz*/
 
 
	IF (ie_opcao_p = 'R') THEN 
		SELECT COUNT(*) 
		INTO STRICT qt_contas_w 
		FROM conta_paciente 
		WHERE nr_atendimento = nr_atendimento_p;
 
		IF (qt_contas_w = 0) THEN 
			dt_retorno_w := dt_alta_w;
		END IF;
		IF (coalesce(dt_intermed_w::text, '') = '') THEN 
			SELECT dt_alta_tesouraria 
			INTO STRICT dt_tesouraria_w 
			FROM atendimento_paciente 
			WHERE nr_atendimento = nr_atendimento_p;
 
			IF (dt_tesouraria_w IS NOT NULL AND dt_tesouraria_w::text <> '') THEN 
				dt_retorno_w := dt_tesouraria_w;
			END IF;
		END IF;
	elsif (ie_opcao_p = 'AT') THEN 
		SELECT dt_alta_tesouraria 
		INTO STRICT DT_TESOURARIA_W 
		FROM ATENDIMENTO_PACIENTE 
		WHERE NR_ATENDIMENTO = NR_ATENDIMENTO_P;
 
		IF (DT_TESOURARIA_W IS NOT NULL AND DT_TESOURARIA_W::text <> '') THEN 
			DT_RETORNO_W := DT_TESOURARIA_W;
		END IF;
	END IF;
 
RETURN	dt_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
