CREATE OR REPLACE FUNCTION obter_se_data_dentro_validade ( nr_prescricao_p bigint, dt_prev_execucao_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   varchar(1);
ds_valida_intervalo_w varchar(1);


BEGIN
  ds_valida_intervalo_w := Obter_Param_Usuario(942, 404, obter_perfil_ativo(), obter_usuario_ativo(), obter_estabelecimento_ativo(), ds_valida_intervalo_w);

  if (coalesce(ds_valida_intervalo_w,'N') = 'S') then
  begin
    select CASE WHEN count(1)=1 THEN 'S'  ELSE 'N' END
    into STRICT ds_retorno_w
    from prescr_medica
    where dt_prev_execucao_p between dt_inicio_prescr and dt_validade_prescr
    and nr_prescricao = nr_prescricao_p;
  end;
  end if;

return  coalesce(ds_retorno_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
