CREATE OR REPLACE FUNCTION obter_se_data_prev_exec_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_data_w			varchar(1) := 'N';
cd_area_procedimento_w		bigint;
cd_especialidade_w		bigint;
cd_grupo_proc_w			bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_tipo_data,'N')
from	rep_regra_data_proc
where	((cd_procedimento = cd_procedimento_p) or (coalesce(cd_procedimento::text, '') = ''))
and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced	= ie_origem_proced_p))
and	((nr_seq_proc_interno = nr_seq_proc_interno_p) or (coalesce(nr_seq_proc_interno::text, '') = ''))
and	((cd_area_procedimento = cd_area_procedimento_w) or (coalesce(cd_area_procedimento::text, '') = ''))
and	((cd_especialidade = cd_especialidade_w) or (coalesce(cd_especialidade::text, '') = ''))
and	((cd_grupo_proc = cd_grupo_proc_w) or (coalesce(cd_grupo_proc::text, '') = ''))
and	((nr_seq_exame = nr_seq_exame_p) or (coalesce(nr_seq_exame::text, '') = ''))
and	((ie_aplicar_exame	= 'S') or (coalesce(nr_seq_exame_p::text, '') = ''))
order by coalesce(nr_seq_proc_interno,0),
	coalesce(cd_procedimento,0),
	coalesce(nr_seq_exame,0),
	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0);



BEGIN

select 	max(cd_area_procedimento),
	max(cd_especialidade),
	max(cd_grupo_proc)
into STRICT	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced 	= ie_origem_proced_p;

open C01;
loop
fetch C01 into
	ie_tipo_data_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_tipo_data_w	:= ie_tipo_data_w;
end loop;
close C01;

return	ie_tipo_data_w;

end;
