CREATE OR REPLACE FUNCTION obter_se_data_prev_real_setor ( nr_atendimento_p bigint, dt_prevista_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
Opção 
Alta real maior que a alta prevista			'M' 
Alta real menor ou igual a alta prevista	'I' 
*/
 
 
				 
ie_retorno_w	varchar(2) := 'N';
dt_alta_w		timestamp;	
 
 

BEGIN 
 
If (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_prevista_p IS NOT NULL AND dt_prevista_p::text <> '') then 
 
	select 	coalesce(max(dt_alta),clock_timestamp()) 
	into STRICT	dt_alta_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	 
	if (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '') then 
	 
	 
		if (ie_opcao_p = 'M') then 
		 
			If (trunc(dt_alta_w) > trunc(dt_prevista_p)) then 
			 
				ie_retorno_w := 'S';	
		 
			end if;
		 
		elsif (ie_opcao_p = 'I') then 
		 
			if (trunc(dt_alta_w) <= trunc(dt_prevista_p)) then 
			 
				ie_retorno_w := 'S';
			 
			end if;
		 
		end if;
		 
	end if;	
	 
	 
end if;
 
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
