CREATE OR REPLACE FUNCTION obter_se_data_tem_hora (dt_campo_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
dt_comparacao_w		timestamp;
qt_diferenca_w		bigint;


BEGIN
ds_retorno_w := 'N';

dt_comparacao_w	:= trunc(dt_campo_p, 'dd');

qt_diferenca_w := abs(dt_comparacao_w - dt_campo_p);

if (qt_diferenca_w > 0) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
