CREATE OR REPLACE FUNCTION obter_se_def_ret_atual (cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_perm_definir_retaguarda_w	regra_perm_panorama.ie_permite_definir_retaguarda%type;


BEGIN

select	max(ie_permite_definir_retaguarda)
into STRICT 	ie_perm_definir_retaguarda_w
from	regra_perm_panorama
where	cd_perfil = cd_perfil_p;

if (coalesce(ie_perm_definir_retaguarda_w::text, '') = '') then  -- se n√£o encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(ie_permite_definir_retaguarda)
	into STRICT 	ie_perm_definir_retaguarda_w
	from	regra_perm_panorama
	where	coalesce(cd_perfil::text, '') = '';
end if;

if (coalesce(ie_perm_definir_retaguarda_w::text, '') = '') then
	ie_perm_definir_retaguarda_w := 'N';
end if;

return ie_perm_definir_retaguarda_w;

end;
